document.addEventListener("DOMContentLoaded",(()=>{!function(e){const t=document.getElementById("user-actions");t&&(t.innerHTML=`\n        <div class="user-profile">\n            <img src="../images/default-avatar.png" alt="User Avatar" class="user-avatar">\n            <span class="user-name">${e.name}</span>\n            <button onclick="logout()">Logout</button>\n        </div>\n        <button onclick="getLocation()">GPS</button>\n    `)}({name:"example@clothify.com".split("@")[0]})})),document.getElementById("login-form")?.addEventListener("submit",(async e=>{e.preventDefault();const t=document.getElementById("email").value,n=document.getElementById("password").value;try{const e=await fetch("http://localhost:3000/login",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({email:t,password:n})}),o=await e.json();e.ok?(alert(o.message),window.location.href="./views/afterlogin.html"):alert(o.message)}catch(e){console.error("Error:",e)}})),document.addEventListener("DOMContentLoaded",(function(){const e=document.getElementById("sign-up-form");e&&e.addEventListener("submit",(async e=>{e.preventDefault();const t=document.getElementById("sign-up-name").value,n=document.getElementById("sign-up-phone").value,o=document.getElementById("sign-up-email").value,a=document.getElementById("sign-up-password").value;try{const e=await fetch("http://localhost:3000/signup",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({full_name:t,phone_number:n,email:o,password:a})}),s=await e.json();e.ok?(alert(s.message),window.location.href="./views/login.html"):alert(s.message)}catch(e){console.error("Error:",e)}}))}));